/**utility functions */
let passback_indexedDB = null;
function indexedDBSet(key, value, theDataBase = "db") {
  return new Promise((resolve, reject) => {
    const dbName = theDataBase;
    const dbVersion = 1;
    const request = indexedDB.open(dbName, dbVersion);
    request.onupgradeneeded = function (event) {
      const db = event.target.result;
      const objectStore = db.createObjectStore("keyValueStore", {
        keyPath: "key",
      });
    };
    request.onsuccess = function (event) {
      const db = event.target.result;
      const transaction = db.transaction(["keyValueStore"], "readwrite");
      const objectStore = transaction.objectStore("keyValueStore");
      const addRequest = objectStore.put({ key: key, value: value });
      addRequest.onsuccess = function () {
        resolve();
      };
      addRequest.onerror = function (event) {
        reject(event.target.error);
      };
      transaction.onerror = function (event) {
        reject(event.target.error);
      };
    };
    request.onerror = function (event) {
      reject(event.target.error);
    };
  });
}
function indexedDBGet(key, theDataBase = "db") {
  return new Promise((resolve, reject) => {
    const dbName = theDataBase;
    const dbVersion = 1;
    const request = indexedDB.open(dbName, dbVersion);
    request.onsuccess = function (event) {
      const db = event.target.result;
      const transaction = db.transaction(["keyValueStore"], "readonly");
      const objectStore = transaction.objectStore("keyValueStore");
      const getRequest = objectStore.get(key);
      getRequest.onsuccess = function (event) {
        const storedValue = event.target.result ? event.target.result.value : null;
        resolve(storedValue);
      };
      getRequest.onerror = function (event) {
        reject(event.target.error);
      };
      transaction.onerror = function (event) {
        reject(event.target.error);
      };
    };
    request.onerror = function (event) {
      reject(event.target.error);
    };
  });
}

/**quietly loads up Bibles in background BEFORE window.onload() (i think... check w/ windsurf AI) */
const putBookIntoIndexedDBenglish = function (_book) {
  const bookScript = document.createElement("script");
  bookScript.src = `https://cdn.jsdelivr.net/gh/Web3Engineer/Bibles@main/${_book}-v3.js`;
  bookScript.async = true;
  bookScript.onload = function () {
    const book = eval(_book);
    indexedDBSet(_book, book);
  };
  document.body.append(bookScript);
  /**mark as stored */
  localStorage.setItem("englishStoredStatus", "stored");
};
if (localStorage.getItem("englishStoredStatus") === null) {
  putBookIntoIndexedDBenglish("english");
}
